mapper.select:
SELECT m1.id AS id, ( CASE WHEN (m2.id = 0 OR m2.id IS NULL) THEN 0 ELSE m2.id END ) AS pId, m1. NAME AS NAME, ( CASE WHEN (m2.id = 0 OR m2.id IS NULL) THEN 'true' ELSE 'false' END ) as isOpen, ( CASE WHEN (m3.ID = 0 OR m3.ID IS NULL) THEN 'false' ELSE 'true' END ) "checked" FROM sys_menu m1 LEFT JOIN sys_menu m2 ON m1.pcode = m2. CODE left join ( SELECT ID FROM sys_menu WHERE ID IN
#{i.menuId}
) m3 on m1.id = m3.id ORDER BY m1.id ASC

select * from sys_menu where 1 = 1
and (name like CONCAT('%',#{model.name},'%') or code like CONCAT('%',#{model.name},'%'))
and levels = #{model.levels}
